function(x :: Real * y_min :: Real * y_max :: Real * x0 :: Real * sx :: Real * se :: Real * d_off :: Real * nu :: Real) => RealInaMo.Examples.ComponentTests.LTypeCalciumStep.cal.act.fsteadyfunction InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.act.fsteady"Inline if necessary"
  input Real x "input value";
  input Real y_min = 0.0 "lower asymptote (fitting parameter)";
  input Real y_max = 1.0 "upper asmyptote when d_off=1 and nu=1 (fititng parameter)";
  input Real x0 = -0.0032 "x-value of sigmoid midpoint when d_off=1 and nu=1 (fitting parameter)";
  input Real sx = 151.285930408472 "scaling factor for x axis (i.e. steepness, fitting parameter)";
  input Real se = 1.0 "scaling factor for exponential part (fitting parameter)";
  input Real d_off = 1.0 "offset in denominator (affects upper asymptote, fitting parameter)";
  input Real nu = 1.0 "reciprocal of exponent of denominator (affects upper asymptote, fitting parameter)";
  output Real y "result";
  protected Real x_adj "adjusted x with offset and scaling factor";
algorithm
  x_adj := sx * (x - x0);
  y := y_min + (y_max - y_min) / (se * exp(-x_adj) + d_off) ^ (1.0 / nu);
end InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.act.fsteady;

function(x :: Real * off :: Real) => RealInaMo.Examples.ComponentTests.LTypeCalciumStep.cal.act.ftaufunction InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.act.ftau"Inline if necessary"
  input Real x "input value";
  input Real off = 0.0 "offset added to result to increase minimum (fitting parameter)";
  output Real y "result of applying the HH-style equation tau = 1/(alpha + beta)";
algorithm
  y := 1.0 / (InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.ftau.falpha(x) + InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.ftau.fbeta(x, 0.005, 10.52 / 0.4, 400.0)) + off;
end InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.act.ftau;

function(x :: Real * x0 :: Real * sy :: Real * sx :: Real) => RealInaMo.Examples.ComponentTests.LTypeCalciumStep.cal.falpha.fafunction InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.falpha.fa"Inline if necessary"
  input Real x "input value";
  input Real x0 = -0.035 "offset for x (fitting parameter)";
  input Real sy = 65.3 "scaling factor for y (fitting parameter)";
  input Real sx = -400.0 "scaling factor for x (fitting parameter)";
  output Real y "result";
  protected Real x_adj "adjusted x with offset and scaling factor";
algorithm
  x_adj := sx * (x - x0);
  if abs(x - x0) < 1e-06 then
    y := sy;
  else
    y := sy * x_adj / (exp(x_adj) - 1.0);
  end if;
end InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.falpha.fa;

