function(x :: Real) => RealInaMo.Examples.ComponentTests.LTypeCalciumStep.cal.ftau.falphafunction InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.ftau.falpha"Inline if necessary"
  input Real x "input value";
  output Real y "output value";
algorithm
  y := InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.falpha.fa(x, -0.035, 26.12 * 2.5, -1000.0 / 2.5) + InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.falpha.fb(x, 0.0, 78.11 / 0.208, -208.0);
end InaMo.Examples.ComponentTests.LTypeCalciumStep.cal.ftau.falpha;

