function(s :: Real * k :: Real) => RealInaMo.Functions.Biochemical.michaelisMentenfunction InaMo.Functions.Biochemical.michaelisMenten"Inline if necessary" "equation for enzymatic reactions following Michaelis-Menten kinetics"
  input Real s(quantity = "Concentration", unit = "mol/m3") "substrate concentration";
  input Real k(quantity = "Concentration", unit = "mol/m3") "concentration producing half-maximum reaction rate (michaelis constant)";
  output Real rate(unit = "1") "reaction rate";
algorithm
  rate := s / (s + k);
end InaMo.Functions.Biochemical.michaelisMenten;
