function(x :: Real * y_min :: Real * y_max :: Real * x0 :: Real * sx :: Real * se :: Real * d_off :: Real * nu :: Real) => RealInaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.act_fast.fsteadyfunction InaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.act_fast.fsteady"Inline if necessary"
  input Real x "input value";
  input Real y_min = 0.0 "lower asymptote (fitting parameter)";
  input Real y_max = 1.0 "upper asmyptote when d_off=1 and nu=1 (fititng parameter)";
  input Real x0 = -0.01022 "x-value of sigmoid midpoint when d_off=1 and nu=1 (fitting parameter)";
  input Real sx = 117.6470588235294 "scaling factor for x axis (i.e. steepness, fitting parameter)";
  input Real se = 1.0 "scaling factor for exponential part (fitting parameter)";
  input Real d_off = 1.0 "offset in denominator (affects upper asymptote, fitting parameter)";
  input Real nu = 1.0 "reciprocal of exponent of denominator (affects upper asymptote, fitting parameter)";
  output Real y "result";
  protected Real x_adj "adjusted x with offset and scaling factor";
algorithm
  x_adj := sx * (x - x0);
  y := y_min + (y_max - y_min) / (se * exp(-x_adj) + d_off) ^ (1.0 / nu);
end InaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.act_fast.fsteady;

function(x :: Real * off :: Real) => RealInaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.act_fast.ftaufunction InaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.act_fast.ftau"Inline if necessary"
  input Real x "input value";
  input Real off = 0.0 "offset added to result to increase minimum (fitting parameter)";
  output Real y "result of applying the HH-style equation tau = 1/(alpha + beta)";
algorithm
  y := 1.0 / (InaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.ftau.falpha(x, 0.0, 39.8, 17.0) + InaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.ftau.fbeta(x, 0.0, -51.0, 0.211)) + off;
end InaMo.Examples.ComponentTests.RapidDelayedRectifierSteady.kr.act_fast.ftau;

