function(x :: Real * x0 :: Real * sy :: Real * sx :: Real) => RealInaMo.Examples.ComponentTests.SodiumChannelSteady.na.act.falphafunction InaMo.Examples.ComponentTests.SodiumChannelSteady.na.act.falpha"Inline if necessary"
  input Real x "input value";
  input Real x0 = -0.0444 "offset for x (fitting parameter)";
  input Real sy = 5829.58 "scaling factor for y (fitting parameter)";
  input Real sx = -78.90791446382072 "scaling factor for x (fitting parameter)";
  output Real y "result";
  protected Real x_adj "adjusted x with offset and scaling factor";
algorithm
  x_adj := sx * (x - x0);
  if abs(x - x0) < 1e-06 then
    y := sy;
  else
    y := sy * x_adj / (exp(x_adj) - 1.0);
  end if;
end InaMo.Examples.ComponentTests.SodiumChannelSteady.na.act.falpha;

function(x :: Real * x0 :: Real * sx :: Real * sy :: Real) => RealInaMo.Examples.ComponentTests.SodiumChannelSteady.na.act.fbetafunction InaMo.Examples.ComponentTests.SodiumChannelSteady.na.act.fbeta"Inline if necessary"
  input Real x "input value";
  input Real x0 = -0.0444 "x-value where y = 1 (fitting parameter)";
  input Real sx = -78.90791446382072 "scaling factor for x axis (fitting parameter)";
  input Real sy = 18400.0 "scaling factor for y axis (fitting parameter)";
  output Real y "result";
algorithm
  y := sy * exp(sx * (x - x0));
end InaMo.Examples.ComponentTests.SodiumChannelSteady.na.act.fbeta;

function(x :: Real * x0 :: Real * sx :: Real * sy :: Real) => RealInaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.falphafunction InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.falpha"Inline if necessary"
  input Real x "input value";
  input Real x0 = -0.0669 "x-value where y = 1 (fitting parameter)";
  input Real sx = -179.5332136445242 "scaling factor for x axis (fitting parameter)";
  input Real sy = 44.9 "scaling factor for y axis (fitting parameter)";
  output Real y "result";
algorithm
  y := sy * exp(sx * (x - x0));
end InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.falpha;

function(x :: Real * y_min :: Real * y_max :: Real * x0 :: Real * sx :: Real * se :: Real * d_off :: Real * nu :: Real) => RealInaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.fbetafunction InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.fbeta"Inline if necessary"
  input Real x "input value";
  input Real y_min = 0.0 "lower asymptote (fitting parameter)";
  input Real y_max = 1491.0 "upper asmyptote when d_off=1 and nu=1 (fititng parameter)";
  input Real x0 = -0.0946 "x-value of sigmoid midpoint when d_off=1 and nu=1 (fitting parameter)";
  input Real sx = 77.51937984496124 "scaling factor for x axis (i.e. steepness, fitting parameter)";
  input Real se = 323.3 "scaling factor for exponential part (fitting parameter)";
  input Real d_off = 1.0 "offset in denominator (affects upper asymptote, fitting parameter)";
  input Real nu = 1.0 "reciprocal of exponent of denominator (affects upper asymptote, fitting parameter)";
  output Real y "result";
  protected Real x_adj "adjusted x with offset and scaling factor";
algorithm
  x_adj := sx * (x - x0);
  y := y_min + (y_max - y_min) / (se * exp(-x_adj) + d_off) ^ (1.0 / nu);
end InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.fbeta;

function(x :: Real) => RealInaMo.Examples.ComponentTests.SodiumChannelSteady.na.inact_fast.fsteadyfunction InaMo.Examples.ComponentTests.SodiumChannelSteady.na.inact_fast.fsteady"Inline if necessary"
  input Real x "input value";
  output Real y "result of applying the HH-style equation steady = alpha/(alpha + beta)";
algorithm
  y := InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.falpha(x, -0.0669, -1000.0 / 5.57, 44.9) / (InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.falpha(x, -0.0669, -1000.0 / 5.57, 44.9) + InaMo.Examples.ComponentTests.SodiumChannelSteady.na.fsteady.fbeta(x, 0.0, 1491.0, -0.0946, 1000.0 / 12.9, 323.3, 1.0, 1.0));
end InaMo.Examples.ComponentTests.SodiumChannelSteady.na.inact_fast.fsteady;

