function(x :: Real * y_min :: Real * y_max :: Real * x0 :: Real * sx :: Real * se :: Real * d_off :: Real * nu :: Real) => RealInaMo.Examples.ComponentTests.TransientOutwardSteady.to.act.fsteadyfunction InaMo.Examples.ComponentTests.TransientOutwardSteady.to.act.fsteady"Inline if necessary"
  input Real x "input value";
  input Real y_min = 0.0 "lower asymptote (fitting parameter)";
  input Real y_max = 1.0 "upper asmyptote when d_off=1 and nu=1 (fititng parameter)";
  input Real x0 = 0.00744 "x-value of sigmoid midpoint when d_off=1 and nu=1 (fitting parameter)";
  input Real sx = 60.97560975609757 "scaling factor for x axis (i.e. steepness, fitting parameter)";
  input Real se = 1.0 "scaling factor for exponential part (fitting parameter)";
  input Real d_off = 1.0 "offset in denominator (affects upper asymptote, fitting parameter)";
  input Real nu = 1.0 "reciprocal of exponent of denominator (affects upper asymptote, fitting parameter)";
  output Real y "result";
  protected Real x_adj "adjusted x with offset and scaling factor";
algorithm
  x_adj := sx * (x - x0);
  y := y_min + (y_max - y_min) / (se * exp(-x_adj) + d_off) ^ (1.0 / nu);
end InaMo.Examples.ComponentTests.TransientOutwardSteady.to.act.fsteady;

function(x :: Real * off :: Real) => RealInaMo.Examples.ComponentTests.TransientOutwardSteady.to.act.ftaufunction InaMo.Examples.ComponentTests.TransientOutwardSteady.to.act.ftau"Inline if necessary"
  input Real x "input value";
  input Real off = 0.000596 "offset added to result to increase minimum (fitting parameter)";
  output Real y "result of applying the HH-style equation tau = 1/(alpha + beta)";
algorithm
  y := 1.0 / (InaMo.Examples.ComponentTests.TransientOutwardSteady.to.ftau.falpha(x, -0.03061, 90.0, 1.037 / 0.003188) + InaMo.Examples.ComponentTests.TransientOutwardSteady.to.ftau.fbeta(x, -0.02384, -120.0, 0.396 / 0.003188)) + off;
end InaMo.Examples.ComponentTests.TransientOutwardSteady.to.act.ftau;

function(x :: Real * x0 :: Real * sx :: Real * sy :: Real) => RealInaMo.Examples.ComponentTests.TransientOutwardSteady.to.ftau.falphafunction InaMo.Examples.ComponentTests.TransientOutwardSteady.to.ftau.falpha"Inline if necessary"
  input Real x "input value";
  input Real x0 = -0.03061 "x-value where y = 1 (fitting parameter)";
  input Real sx = 90.0 "scaling factor for x axis (fitting parameter)";
  input Real sy = 325.2823086574655 "scaling factor for y axis (fitting parameter)";
  output Real y "result";
algorithm
  y := sy * exp(sx * (x - x0));
end InaMo.Examples.ComponentTests.TransientOutwardSteady.to.ftau.falpha;

